mapper.select:
select e.*,d.name deptName from emp e left join dept d on e.dept_id = d.id


    and e.name like concat('%',#{name},'%')


    and e.gender = #{gender}


    and e.update_time between #{begin} and #{end}


order by e.update_time desc

mapper.update:
UPDATE emp
username=#{username}, password=#{password}, name=#{name}, gender=#{gender}, phone=#{phone}, job=#{job}, salary=#{salary}, image=#{image}, entry_date=#{entryDate}, dept_id=#{deptId}, update_time=#{updateTime},
where id=#{id}
